from gne import GeneralNewsExtractor
import re
def extract_content(html):
    extractor=GeneralNewsExtractor()
    html="<html><body>"+html+"</body></html>"
    r=extractor.extract(html)
    r['content'].replace("'", "").replace("\n", "")
    return r['content']
